import React from 'react'
import DeleteIcon from '@material-ui/icons/Delete';

const Note = (props)=>{
    const delnote=()=>{
        props.deleitem(props.id)
    }

    const setInputheight=(element,defaultheight)=>{
        if(element){
            const target= element.target? element.target :element
            target.style.height=defaultheight
            target.style.height=`${target.scrollHeight}px`
        }

    }
    return(<>
    <div className="note1">
    <h1>{props.title}</h1>
    <br/>
    <textarea onChange={(event)=>setInputheight(event, "100px")} row="6" column="" wrap="physical" >{props.txtar}</textarea>
    <button className="delbtn" onClick={delnote}><DeleteIcon /> </button>
    </div>
    </>
   )
}
export default Note
