import React, { useState } from 'react'

import AddIcon from '@material-ui/icons/Add';
const CreatNote = (props) => {

    const [note, updnote]=useState({
   title:"",
   txtar:""

    })
// for store what we type in input to an array
    const addnote=(event)=>{
        const{value, name}=event.target


        updnote((prevalue)=>{ 
            return ({
                ...prevalue,
                [name]:value

            })
        })
    }

    //for render save data when button click


    const saveNote=(props)=>{
   props.passnote(note)
    }

    return (<>
        <div className="main_note">
            <form >
                <div className="creatNote">
                    <input type="text" placeholder="Title" autoComplete="off"  name="title" value={note.title} onChange={addnote}/>
                    <textarea rows="2" cols="" wrap="physical" placeholder="Take a note..." name="txtar" value={note.txtar} onChange={addnote} />
                    <button className="plus_sign" onClick={saveNote} ><AddIcon  /></button>
                </div>

            </form>

        </div>
    </>

    )


}
export default CreatNote
