import Header from './Header'
import Footer from './Footer'
import CreatNote from './CreatNote'
import Note from './Note'
import { useState } from 'react'

const App = () => {
    const [additem, updadditm] = useState([])


    const addnote1 = (note) => {
        alert('i am clicked')
        updadditm((prevalue) => {
            return [ ...prevalue, note ]
        })
        console.log(note)
    }
    return (<>
        <Header />
        <CreatNote passNote={addnote1} />

        {additem.map((val,index)=>{
            return <Note
                key={index}
                id={index}
                title={val.title}
                txtar={val.txtar}
            />


        })}
        <Footer/>
    </>
    )
}
export default App
